mapper.select:
SELECT l.target_id, CASE WHEN l.target_type = 'PLACE' THEN p.title WHEN l.target_type = 'GOODS' THEN g.title WHEN l.target_type = 'PACKAGE' THEN pkg.title ELSE NULL END AS targetName FROM `like` l LEFT JOIN place p ON l.target_type = 'PLACE' AND l.target_id = p.place_id LEFT JOIN goods g ON l.target_type = 'GOODS' AND l.target_id = g.goods_id LEFT JOIN package pkg ON l.target_type = 'PACKAGE' AND l.target_id = pkg.package_id WHERE l.user_id = #{userId}
AND l.target_type = #{req.targetType}
ORDER BY l.like_id DESC LIMIT #{req.limit} OFFSET #{req.offset}
