mapper.select:
SELECT a.order_history_type , DATE_FORMAT(b.ordered_at, '%Y-%m') AS orderedAt , SUM(a.quantity) AS totalQuantity , SUM(a.quantity * c.price) AS totalPrice FROM order_history a JOIN `order` b ON a.order_id = b.order_id JOIN goods c ON a.target_id = c.goods_id WHERE a.order_history_type = 'GOODS' GROUP BY orderedAt
UNION ALL
SELECT a.order_history_type , DATE_FORMAT(b.ordered_at, '%Y-%m') AS orderedAt , SUM(a.quantity) AS totalQuantity , SUM(a.quantity * d.price) AS totalPrice FROM order_history a JOIN `order` b ON a.order_id = b.order_id JOIN package d ON a.target_id = d.package_id WHERE a.order_history_type = 'PACKAGE' GROUP BY orderedAt ORDER BY orderedAt LIMIT #{ limit } OFFSET #{ offset };

SELECT a.order_history_type , DATE_FORMAT(b.ordered_at, '%Y-%m-%d') AS orderedAt , c.title , SUM(a.quantity) AS totalQuantity , SUM(a.quantity * c.price) AS totalPrice FROM order_history a JOIN `order` b ON a.order_id = b.order_id JOIN goods c ON a.target_id = c.goods_id WHERE a.order_history_type = 'GOODS' GROUP BY orderedAt
UNION ALL
SELECT a.order_history_type , DATE_FORMAT(b.ordered_at, '%Y-%m-%d') AS orderedAt , d.title , SUM(a.quantity) AS totalQuantity , SUM(a.quantity * d.price) AS totalPrice FROM order_history a JOIN `order` b ON a.order_id = b.order_id JOIN package d ON a.target_id = d.package_id WHERE a.order_history_type = 'PACKAGE' GROUP BY orderedAt ORDER BY orderedAt LIMIT #{ limit } OFFSET #{ offset };
