mapper.select:
SELECT p.package_id, p.title, p.price, p.area, p.start_date, p.end_date, COUNT(DISTINCT l.like_id) AS like_count, COUNT(DISTINCT r.review_id) AS review_count FROM package p LEFT JOIN `like` l on p.package_id = l.target_id AND l.target_type = 'PACKAGE' LEFT JOIN review r ON p.package_id = r.target_id AND r.target_type = 'PACKAGE' AND r.is_deleted = 'N'
p.is_deleted = 'N'
area = #{area}
AND start_date >= #{startDate}
AND end_date <= #{endDate}
AND title LIKE CONCAT('%', #{ title }, '%')
GROUP BY p.package_id
ORDER BY like_count DESC
ORDER BY review_count DESC
ORDER BY p.start_date ASC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM package

    is_deleted = 'N'

        AND area = #{area}


        AND start_date >= #{startDate}


        AND end_date <= #{endDate}


        AND title LIKE CONCAT('%', #{title}, '%')
