mapper.select:
SELECT a.place_id, a.title, a.detail, a.image, a.address, a.location, a.rest_date, a.category FROM place a JOIN area b ON (a.area_id = b.area_id)
AND b.parent_area_id = #{parentAreaId}
AND b.area_id = #{areaId}
AND a.category = #{category}
AND a.title LIKE CONCAT('%', #{ title }, '%')
ORDER BY a.title LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM place a
JOIN area b ON (a.area_id = b.area_id)


        AND b.parent_area_id = #{parentAreaId}


        AND b.area_id = #{areaId}


        AND a.category = #{category}


        AND a.title LIKE CONCAT('%', #{ title }, '%')
